import { useState } from "react";
import "../styles/Cinema.css";

const cinemas = [
  {
    id: 1,
    name: "Paragon Cineplex",
    showtimes: {
      "2025-11-17": ["10:30", "13:00", "16:45", "20:00"],
      "2025-11-18": ["11:00", "14:00", "18:00"],
    },
  },
  {
    id: 2,
    name: "Major Ratchayothin",
    showtimes: {
      "2025-11-17": ["09:00", "12:00", "15:30", "19:15"],
      "2025-11-18": ["10:00", "13:30", "17:00"],
    },
  },
];

function Cinema() {
  const [selectedCinema, setSelectedCinema] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);

  return (
    <div className="cinema-page">
      <h1>เลือกโรงภาพยนตร์</h1>

      {/* STEP 1: เลือกโรง */}
      <div className="cinema-list">
        {cinemas.map((c) => (
          <button
            key={c.id}
            className={`cinema-btn ${selectedCinema?.id === c.id ? "active" : ""}`}
            onClick={() => {
              setSelectedCinema(c);
              setSelectedDate(null);
            }}
          >
            {c.name}
          </button>
        ))}
      </div>

      {/* STEP 2: เลือกวัน */}
      {selectedCinema && (
        <>
          <h2>เลือกวันที่</h2>
          <div className="date-list">
            {Object.keys(selectedCinema.showtimes).map((date) => (
              <button
                key={date}
                className={`date-btn ${selectedDate === date ? "active" : ""}`}
                onClick={() => setSelectedDate(date)}
              >
                {date}
              </button>
            ))}
          </div>
        </>
      )}

      {/* STEP 3: เลือกรอบ */}
      {selectedDate && (
        <>
          <h2>เลือกรอบฉาย</h2>
          <div className="time-list">
            {selectedCinema.showtimes[selectedDate].map((time) => (
              <button key={time} className="time-btn">
                {time}
              </button>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default Cinema;
